  static String getPlayer(String id) {
    String player = (_PLAYERS.containsKey(id) ? _PLAYERS[id] : _PLAYERS["91c80d77"])!;
    return player;
  }

  static String shortenCode(String str) {
    return str
        .replaceAll(RegExp(r'\s{2,}'), '')
        .replaceAllMapped(
            RegExp(r'[\n\r\t\s]*([,{}=;:()?|&])[\n\r\t\s]*'),
            (Match m) => "${m[1]}"
        )
        .replaceAll(RegExp(r';}'), '}');
  }

  
  static String initPlayer() {
    return "((w)=>{const p=(document.querySelector('svg')||{}).svgatorPlayer;if(!p||!w.SVGatorFlutterChannel){return}const e=['reverse','pause','restart','stop','end','play'];const _map=()=> e.forEach(function(e){p.on(e,function(o){w.SVGatorFlutterChannel.postMessage(JSON.stringify({'event':e,'offset':o}))})});if(p.totalTime){_map()}else{p.ready(_map)}})(window);";
  }

  static String _generateCommand(String command, dynamic param, String? property) {
    var commandStr = '';
    if (property != '' && property != null) {
      commandStr = "((c,p,v)=>{const l=()=>(document.querySelector('svg')||{}).svgatorPlayer;if(!l()){return}l().ready(()=> l()[c].call(l,p||'',v||''))})('$command','$property',$param);";
    } else {
      commandStr = "((c,p)=>{const l=()=>(document.querySelector('svg')||{}).svgatorPlayer;if(!l()){return}l().ready(()=> l()[c].call(l,p||''))})('$command',$param);";
    }
    return commandStr;
  }

  static void runCommand(
    WebViewController? SVGatorController,
    String command,
    dynamic param,
    String? property)
  {
    SVGatorController?.runJavascript(_SVGatorPlayer._generateCommand(command, param, property));
  }
  

  static String wrapPage(String svg) {
    const String header = '''<!doctype html>
    <html>
      <head>
        <meta name="viewport" content="width=device-width, initial-scale=1, shrink-to-fit=no">
        <style>
            html, body {
                height: 100%;
                overflow: hidden;
                width: 100%;
            }
            body {
                margin: 0;
                padding: 0;
            }
            svg {
                height: 100%;
                left: 0;
                position: fixed;
                top: 0;
                width:100%;
            }
        </style>
      </head>
      <body>
    ''';
    const String footer = '''
      </body>
      </html>''';
    return shortenCode(header) + svg + shortenCode(footer);
  }
